type GetJobsParams = {
  skip?: number;
  limit?: number;
  allow_for_translated_jobs?: boolean;
  is_hot?: boolean;
  country?: string;
};

export async function getJobs({
  skip = 0,
  limit = 5,
  allow_for_translated_jobs = false,
  is_hot = false,
  country,
}: GetJobsParams): Promise<Job[]> {
  const data = is_hot ? hotJobs : jobs;
  const paginatedData = data.slice(skip, skip + limit);

  return new Promise<Job[]>((resolve) => {
    setTimeout(() => {
      resolve(paginatedData);
    }, 2000); // 4 seconds delay
  });
}

export async function getJobById(jobId: string): Promise<Job> {
  if (!jobId) {
    // can return 404 heres
    return {} as Job;
  }
  const allJobs = [...jobs, ...hotJobs];
  const job = allJobs.find((job) => job.id.toString() === jobId);
  if (job) return job;

  return {} as Job;
}

